import React from 'react';
import { loadModules, loadCss } from 'esri-loader';



export class WebMapView extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    loadCss('https://js.arcgis.com/4.13/esri/themes/dark-blue/main.css');

    loadModules([
      'esri/WebScene',
      'esri/views/SceneView',
      'esri/widgets/Search',
      'esri/layers/Layer',
      'esri/widgets/Legend',
      'esri/widgets/LayerList',
      'esri/widgets/Expand',
      "esri/PopupTemplate"
    ])
      .then(([
        WebScene,
        SceneView,
        Search,
        Legend,
        LayerList,
        Expand,
        PopupTemplate,
        Layer
      ]) => {
        const map = new WebScene({
          portalItem: {
            // autocasts as new PortalItem()
            id: "ac68b33deea84c15bc26f408f3c413fd",
            portal: {
              url: "https://cbs.mugla.bel.tr/portal"
            },
          }
        });

        this.view = new SceneView({
          container: this.mapRef.current,
          map: map,
        });

        var search = new Search({
          view: this.view
        });

        var Katmanlar = new LayerList({ view: this.view });

        var LayerExpand = new Expand({
          view: this.view,
          content: Katmanlar,
        });

        var legend = new Legend({
          view: this.view,
          layerInfos: [{
            layer: map,
          }]
        });

        var LegendExpand = new Expand({
          view: this.view,
          content: legend,
        });


        var pTemplate = {  
          content: "deneme",  
          title: "deneme"  
        };  



        var droneLyr = Layer.fromPortalItem({
          portalItem: {
            // autocasts as new PortalItem()
            id: "ac68b33deea84c15bc26f408f3c413fd"
          }
        })

        console.log(droneLyr)

        droneLyr.popupTemplate = pTemplate;

        this.view.ui.add(search, "top-right");
        this.view.ui.add(LegendExpand, "top-right");
        this.view.ui.add(LayerExpand, "top-right");

      });
  }

  render() {
    return (
      <div className="webmap" ref={this.mapRef} style={{ width: '100vw', height: '100vh' }} />
    );
  }
}
