import React from 'react';
import { loadModules, loadCss } from 'esri-loader';



export class WebMapView extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    loadCss('https://js.arcgis.com/4.13/esri/themes/dark-blue/main.css');

    loadModules([
      'esri/Map',
      'esri/WebScene',
      'esri/views/SceneView',
      'esri/layers/SceneLayer',
      'esri/layers/FeatureLayer',
      'esri/widgets/Search',
      'esri/widgets/Legend',
      'esri/layers/Layer',
      'esri/widgets/LayerList',
      'esri/core/Collection',
      'esri/widgets/Expand',
      "esri/PopupTemplate",
      'esri/portal/PortalItem'
    ])
      .then(([
        Map,
        WebScene,
        SceneView,
        SceneLayer,
        FeatureLayer,
        Search,
        Legend,
        Layer,
        LayerList,
        Collection,
        Expand,
        PopupTemplate,
        PortalItem
      ]) => {
        var map = new Map({
          basemap: "satellite",
          ground: "world-elevation"  
        });

        var yollar = new FeatureLayer({
          title : "Yollar",
          url: "https://muglacbs.mugla.bel.tr/cbs/rest/services/MABS/Mabs_View_Yol/MapServer/0",
          popupTemplate: {  
            title: "Yol",
            content: "Yol Content" 
          },

        });

        map.add(yollar);

        var binalar = new SceneLayer({
          title: "Binalar",
          url: "https://cbs.mugla.bel.tr/cbs/rest/services/Hosted/Yapilar3D_2/SceneServer",
          // outFields: ["KATADEDI"],
          // popupTemplate: {  // Enable a popup
          //     title: "Bina", // Show attribute value
          //     content: "Kat Adedi : {KATADEDI}" // Display in pop-up
          // },
          definitionExpression: "KATADEDI > 0",

        });

        map.add(binalar);

        this.view = new SceneView({
          container: this.mapRef.current,
          map: map,
          camera: {
            position: { 
              x: 28.365316,
              y: 37.150000,
              z: 15000 
            },
            tilt: 25 
          }
        });

        var symbolbinalar = {
          type: "mesh-3d",
          symbolLayers: [
            {
              type: "fill", 
              material: {
                color: [220, 154, 54]
              }
            }
          ]
        };

        binalar.renderer = {
          type: "simple", 
          symbol: symbolbinalar
        };

        var symbolyollar = {
          symbolLayers: [
            {
              type: "fill", 
              material: {
                color: [245, 245, 177]
              }
            }
          ]
        };

        yollar.renderer = {
          type: "simple", 
          symbol: symbolyollar
        };

        var search = new Search({
          view: this.view
        });

        var Katmanlar = new LayerList({
          view: this.view,
        });

        var LayerExpand = new Expand({
          view: this.view,
          content: Katmanlar,
        });

        var legend = new Legend({
          view: this.view,
          layerInfos: [{
            layer: map,
          }]
        });

        var LegendExpand = new Expand({
          view: this.view,
          content: legend,
        });

        this.view.ui.add(search, "top-right");
        this.view.ui.add(LegendExpand, "top-right");
        this.view.ui.add(LayerExpand, "top-right")

      });
  }

  render() {
    return (
      <div className="webmap" ref={this.mapRef} style={{ width: '100vw', height: '100vh' }} />
    );
  }
}
