import React from 'react';
import { loadModules, loadCss } from 'esri-loader';



export class WebMapView extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    loadCss('https://js.arcgis.com/4.13/esri/themes/dark-blue/main.css');

    loadModules([
      'esri/WebScene',
      'esri/views/SceneView',
      'esri/layers/SceneLayer',
      'esri/widgets/Search',
      'esri/widgets/Legend',
      'esri/layers/Layer',
      'esri/widgets/LayerList',
      'esri/core/Collection',
      'esri/widgets/Expand',
      "esri/PopupTemplate",
      'esri/portal/PortalItem'
    ])
      .then(([
        WebScene,
        SceneView,
        SceneLayer,
        Search,
        Legend,
        Layer,
        LayerList,
        Collection,
        Expand,
        PopupTemplate,
        PortalItem
      ]) => {
        var map = new Map({
          basemap: "satellite",
          ground: "world-elevation"  // show elevation
        });

        var view = new SceneView({
          container: "viewDiv",
          map: map,
          camera: {
            position: {  // observation point
              x: 28.365316,
              y: 37.150000,
              z: 15000 // altitude in meters
            },
            tilt: 25  // perspective in degrees
          }
        });

        var yollar = new FeatureLayer({
          url: "https://muglacbs.mugla.bel.tr/cbs/rest/services/MABS/Mabs_View_Yol/MapServer/0",
          popupTemplate: {  // Enable a popup
            title: "Yol", // Show attribute value
            content: "Yol Content" // Display in pop-up
          },

        });

        map.add(yollar);

        var binalar = new SceneLayer({
          url: "https://cbs.mugla.bel.tr/cbs/rest/services/Hosted/Yapilar3D_2/SceneServer",
          // outFields: ["KATADEDI"],
          // popupTemplate: {  // Enable a popup
          //     title: "Bina", // Show attribute value
          //     content: "Kat Adedi : {KATADEDI}" // Display in pop-up
          // },
          definitionExpression: "KATADEDI > 0",

        });

        map.add(binalar);

        var search = new Search({
          view: this.view
        });

        var Katmanlar = new LayerList({
          view: this.view,
        });

        var LayerExpand = new Expand({
          view: this.view,
          content: Katmanlar,
        });

        var legend = new Legend({
          view: this.view,
          layerInfos: [{
            layer: map,
          }]
        });

        var LegendExpand = new Expand({
          view: this.view,
          content: legend,
        });

        var binalarpopup = new PopupTemplate({
          title: "dwfw",
          content: " people live in this census tract"
        });

        this.view.ui.add(search, "top-right");
        this.view.ui.add(LegendExpand, "top-right");
        this.view.ui.add(LayerExpand, "top-right")

      });
  }

  render() {
    return (
      <div className="webmap" ref={this.mapRef} style={{ width: '100vw', height: '100vh' }} />
    );
  }
}
