import React from 'react';
import { loadModules, loadCss } from 'esri-loader';



export class WebMapView extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    loadCss('https://js.arcgis.com/4.13/esri/themes/dark-blue/main.css');

    loadModules(['esri/WebScene', 'esri/views/SceneView', 'esri/widgets/Search', 'esri/widgets/Legend', 'esri/widgets/LayerList','esri/widgets/Expand'])
      .then(([WebScene, SceneView, Search, Legend, LayerList,Expand]) => {
        const map = new WebScene({
          portalItem: {
            // autocasts as new PortalItem()
            id: "ac68b33deea84c15bc26f408f3c413fd",
            portal: {
              url: "https://cbs.mugla.bel.tr/portal"
            },
          }
        });

        this.view = new SceneView({
          container: this.mapRef.current,
          map: map,
        });

        var search = new Search({
          view: this.view
        });


        const layerList = new LayerList({
          view: this.view,
          listItemCreatedFunction: function(event){
            const item = event.item;
            // displays the legend for each layer list item
            item.panel = {
              content: "legend"
            };
          }
        });

        var Katmanlar = new LayerList({ view: this.view });

        var LayerExpand = new Expand({
          view: this.view,
          content: Katmanlar
        });

        // Add the expand instance to the ui


        // var legend = new Legend({
        //   view: this.view,
        //   layerInfos: [{
        //     layer: map,
        //     title: "Legend"
        //   }]
        // });

        // legend.basemapLegendVisible = true;
        this.view.ui.add(search, "top-right");
        // this.view.ui.add(legend, "buttom-right");
        this.view.ui.add(LayerExpand, "top-right" );

      });
  }

  render() {
    return (
      <div className="webmap" ref={this.mapRef} style={{ width: '100vw', height: '100vh' }} />
    );
  }
}
