import React from 'react';
import { loadModules, loadCss } from 'esri-loader';



export class WebMapView extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    loadCss('https://js.arcgis.com/4.13/esri/themes/dark-blue/main.css');

    loadModules([
      'esri/WebScene',
      'esri/views/SceneView',
      'esri/layers/SceneLayer',
      'esri/widgets/Search',
      'esri/widgets/Legend',
      'esri/layers/Layer',
      'esri/widgets/LayerList',
      'esri/core/Collection',
      'esri/widgets/Expand',
      "esri/PopupTemplate",
      'esri/portal/PortalItem'
    ])
      .then(([
        WebScene,
        SceneView,
        SceneLayer,
        Search,
        Legend,
        Layer,
        LayerList,
        Collection,
        Expand,
        PopupTemplate,
        PortalItem
      ]) => {
        const map = new WebScene({
          portalItem: {
            id: "ac68b33deea84c15bc26f408f3c413fd",
            portal: {
              url: "https://cbs.mugla.bel.tr/portal"
            },
          }
        });

        this.view = new SceneView({
          container: this.mapRef.current,
          map: map,
        });

        var search = new Search({
          view: this.view
        });

        var Katmanlar = new LayerList({ 
          view: this.view,
        });

console.log(Katmanlar)

        var LayerExpand = new Expand({
          view: this.view,
          content: Katmanlar,
        });

        var legend = new Legend({
          view: this.view,
          layerInfos: [{
            layer: map,
          }]
        });

        var LegendExpand = new Expand({
          view: this.view,
          content: legend,
        });

        var binalar = new SceneLayer({
          // URL to the service
          url: "https://cbs.mugla.bel.tr/cbs/rest/services/Hosted/Yapilar3D_2/SceneServer/layers/1"
        });

        var binalarpopup =  new PopupTemplate({
          title : "dwfw",
          content: " people live in this census tract"
        });


        binalar.popupTemplate = binalarpopup;

        console.log(binalar)

        this.view.ui.add(search, "top-right");
        this.view.ui.add(LegendExpand, "top-right");
        this.view.ui.add(LayerExpand, "top-right")

      });
  }

  render() {
    return (
      <div className="webmap" ref={this.mapRef} style={{ width: '100vw', height: '100vh' }} />
    );
  }
}
