import React from 'react';
import LogoImage from '../logo.png'
import { loadModules, loadCss } from 'esri-loader';



export class WebMapView extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    loadCss('https://js.arcgis.com/4.13/esri/themes/dark-blue/main.css');

    loadModules([
      'esri/Map',
      'esri/WebScene',
      'esri/views/SceneView',
      'esri/layers/SceneLayer',
      'esri/layers/FeatureLayer',
      'esri/widgets/Search',
      'esri/widgets/Legend',
      'esri/layers/Layer',
      'esri/widgets/LayerList',
      'esri/core/Collection',
      'esri/widgets/Expand',
      "esri/PopupTemplate",
      'esri/portal/PortalItem',
      'esri/widgets/Locate',
      'esri/Graphic'
    ])
      .then(([
        Map,
        WebScene,
        SceneView,
        SceneLayer,
        FeatureLayer,
        Search,
        Legend,
        Layer,
        LayerList,
        Collection,
        Expand,
        PopupTemplate,
        PortalItem,
        Locate,
        Graphic
      ]) => {
        var map = new Map({
          basemap: "satellite",
          ground: "world-elevation"  
        });

        var yollar = new FeatureLayer({
          title : "Yollar",
          url: "https://muglacbs.mugla.bel.tr/cbs/rest/services/MABS/Mabs_View_Yol/MapServer/0",
          outFields: ["YOLADI","YOLTIP"],
          popupTemplate: {  
            title: "Yol",
            content:  "<p>Yol Tipi : {YOLTIP}</p><p>Yol Ad覺 : {YOLADI}</p>"
          },

        });

        map.add(yollar);

        var binalar = new SceneLayer({
          title: "Binalar",
          url: "https://cbs.mugla.bel.tr/cbs/rest/services/Hosted/Yapilar3D_2/SceneServer",
          outFields: ["KATADEDI","AD","KIMLIKNO","TIP","DURUM"],
          popupTemplate: {  // Enable a popup
              title: "Bina", // Show attribute value
              content: "<p>Tip : {TIP}</p><p>Durum : {DURUM}</p><p>Bina Ad覺 : {AD}</p><p>Kimlik No : {KIMLIKNO}</p><p>Kat Adedi : {KATADEDI}</p>"
          },
          definitionExpression: "KATADEDI > 0",

        });

        map.add(binalar);

        this.view = new SceneView({
          container: this.mapRef.current,
          map: map,
          camera: {
            position: { 
              x: 28.365316,
              y: 37.150000,
              z: 15000 
            },
            tilt: 25 
          }
        });

        var symbolbinalar = {
          type: "mesh-3d",
          symbolLayers: [
            {
              type: "fill", 
              material: {
                color: [255, 170, 2]
              },
              edges: {
                type: "solid", 
                color: "black"
              }
            },
          ]
        };

        binalar.renderer = {
          type: "simple", 
          symbol: symbolbinalar
        };

        var search = new Search({
          view: this.view
        });

        var Katmanlar = new LayerList({
          view: this.view,
        });

        var LayerExpand = new Expand({
          view: this.view,
          content: Katmanlar,
        });

        var legend = new Legend({
          view: this.view,
          layerInfos: [{
            layer: map,
          }]
        });

        var LegendExpand = new Expand({
          view: this.view,
          content: legend,
        });

        var locateWidget = new Locate({
          view: this.view,
          graphic: new Graphic({
            symbol: { type: "simple-marker" } 
          })
        });

        this.view.ui.add("logoDiv", "bottom-right");
        this.view.ui.add(locateWidget, "top-left");
        this.view.ui.add(search, "top-right");
        this.view.ui.add(LegendExpand, "top-right");
        this.view.ui.add(LayerExpand, "top-right")

      });
  }

  render() {
    return (
      <div className="webmap" ref={this.mapRef} style={{ width: '100vw', height: '100vh' }}>
         <div id="logoDiv" className="esri-widget" style={ {backgroundImage: `url(${LogoImage})`, textAlign: "center",
        width: '400px' }}>dwdw</div>
      </div>
    );
  }
}
